<?php $conn = mysqli_connect("localhost", "root", "", "loginpage"); if(!$conn){ die("Connection Failed"); } $username = $_POST['username']; $email = $_POST['email']; $phone = $_POST['phone']; $password = $_POST['password']; $confirm_password = $_POST['confirm_password']; /* Check if passwords match */ if($password != $confirm_password){ echo "Passwords do not match!"; exit(); } $sql = "INSERT INTO users (username, email, phone, password, confirm_password) VALUES ('$username', '$email', '$phone', '$password', '$confirm_password')"; if(mysqli_query($conn, $sql)){ echo "Signup Successful!"; } else { echo "Error: " . mysqli_error($conn); } mysqli_close($conn); ?>